import React from "react";
import PropTypes from "prop-types";

/**
 * A square in the game of tic tac toe.   Can be clicked or the square can contain a value.
 */
const Square = ({ onClick, value, winningLine }) => {
  if (winningLine) {
    winningLine.map((num) => {
      return (
        <button className="square winning-square" onClick={onClick}>
          {value}
        </button>
      );
    });
  } else
    return (
      <button className="square" onClick={onClick}>
        {value}
      </button>
    );
};

Square.propTypes = {
  /**
   *  The handler for when a square is clicked
   */
  onClick: PropTypes.func,

  /**
   *  The value to put in the square
   */
  value: PropTypes.string,
};

export default Square;
